var http = require('http');
const common = require('@monorepo-services/common');
var app = http.createServer((req, res) => {
  res.writeHead(200, {
    'Content-Type': 'text/json',
    'Access-Control-Allow-Origin': '*',
    'X-Powered-By': 'nodejs'
  });
  res.write(JSON.stringify({ name: 'service-1', common: common(1) }));
  res.end();
});
app.listen(8081);
